mapper.select:
select B.* from(
        select rownum as rn, A.* from(
            select m.midx,r.ridx,rpoint,rcontent,rlike,m.mnickname from POLY_REVIEW r,POLY_MEMBER m where m.midx=r.midx and r.rDel_yn='N' and r.cidx=#{cidx} order by rLike desc, ridx desc
            )A
    )B where B.rn <=5
